package Lecture1;

public class MathematicsOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=29;
		int c=a+b;
		System.out.println(c);
		c = a-b;
		System.out.println(c);
		int d=a/b;
		System.out.println(d);
		d=a%b;
		System.out.println(d);
		d=a*b;
		System.out.println(d);

	}

}
